import React, { useEffect } from "react"
import { useLocation } from "react-router-dom"
import "./videochat.css"
import InviteAlert from "../invitealert/invitealert"
import { Stack } from "grommet"
import { ToastContainer } from "react-toastify"

function VideoChat() {
  useEffect(() => {
    setupJitsi()
  })
  const location = useLocation()
  function setupJitsi() {
    const domain = "meet.jit.si"
    const config = {
      TOOLBAR_BUTTONS: [
        'microphone', 'camera', 'closedcaptions', 'desktop', 'fullscreen',
        'fodeviceselection', 'hangup', 'profile', 'chat', 'recording',
        'livestreaming', 'etherpad', 'sharedvideo', 'settings', 'raisehand',
        'videoquality', 'filmstrip', 'feedback', 'invite', 'stats', 'shortcuts',
        'tileview', 'videobackgroundblur', 'download', 'help', 'mute-everyone'
      ],
    }
    const options = {
      roomName: location.pathname,
      height: "100%",
      width: "100%",
      parentNode: document.getElementById('jitsi-container'),
      interfaceConfigOverwrite: config,
    }

    const api = new window.JitsiMeetExternalAPI(domain, options);
    const chatName = location.state.chatName
    api.executeCommand('subject', chatName);

  }
  return (
    <Stack>

      <div className="container">
        <InviteAlert />
        <ToastContainer
          position="top-right"
          autoClose={5000}
          hideProgressBar={false}
          newestOnTop={false}
          closeOnClick
          rtl={false}
          pauseOnVisibilityChange
          draggable />
      </div>
      <div>
        <div
          id="jitsi-container"
          className="jitsi-container"
        />
      </div>
    </Stack>
  )
}

export default VideoChat;
