import React, { useState } from "react"
import "./createchat.css"
import { Box, Form, FormField, Button } from 'grommet'
import { useHistory } from "react-router-dom"


const CREATE_CHAT_TEXT = "Start a chat room and share with your friends!"
const BACKEND_URL = process.env.REACT_APP_BACKEND_URL
const NEW_CHAT_ID_ENDPOINT = BACKEND_URL + "/newchatroomid"
const ROOM_PREFIX = "/rooms/"

const CreateChat = () => {
  const [chatName, setChatName] = useState("")
  const history = useHistory()
  const onTextChange = (e) => {
    setChatName(e.target.value)
  }
  const onSubmit = () => {
    fetch(NEW_CHAT_ID_ENDPOINT).then((response) => {
      return response.json()
    }).then((id) => {
      const url = ROOM_PREFIX + id
      history.push(url, { chatName: chatName })
    })
  }
  return (
    <Box
      direction='column'
      align='center'
      backgroun='brand'
    >
      <Form>
        <FormField name="Chat Room Name" label={CREATE_CHAT_TEXT} onChange={onTextChange} />
        <Button type="submit" primary label="Get Started!" onClick={onSubmit} />
      </Form>
    </Box>
  )
}

export default CreateChat;
