import React from "react"
import { Heading, Text } from "grommet"
import CreateChat from "../createchat/createchat"
import "./mainpage.css"

const TITLE = "Support The Fight Against COVID-19 While Sheltering In Place"
const BOLD_DONATION_TEXT = "100% of the ad revenue will be donated to the United Way Worldwide's COVID-19 Community Response and Recovery Fund."
const BOLD_REVENUE = (
    <b>{BOLD_DONATION_TEXT}</b>
)
const DESC = `Do some good while talking face-to-face with your loved ones. This website provides a free videochat tool with some non-intrusive ads. 
 Stay safe, shelter in place, 
and help those on the frontlines of the coronavirus crisis.`
const MainPage = () => {
    return (
        <div className="container">
            <div className="title-container">
                <Heading level="1">
                    {TITLE}
                </Heading>
                <Text>
                    {DESC}
                </Text>
                <CreateChat />
            </div>

        </div>
    )
}

export default MainPage;
